<?php

namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => 'dev-master',
    'version' => 'dev-master',
    'aliases' => 
    array (
    ),
    'reference' => '56b2bc4bb936babef8c11b360e7e7ff5ed37c314',
    'name' => 'wpengine/autoupdater',
  ),
  'versions' => 
  array (
