      'version' => '2.0.12.0',
      'aliases' => 
      array (
      ),
      'reference' => '258c89a6b97de7dfaf5b8c7607d0478e236b04fb',
    ),
    'paragonie/sodium_compat' => 
    array (
      'pretty_version' => 'v1.14.0',
      'version' => '1.14.0.0',
      'aliases' => 
